    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
                                                                    //CM TO INCH
        try {
            String Cm, Sum;                                         //defining the variables
            Double Inch = 0.39;                                     //double value of an Inch       
            Cm = JOptionPane.showInputDialog("Cm to Inch");         //displays an input dialog for the user to input the CM value           
            Sum = String.valueOf(Inch * Double.valueOf(Cm));        //multiply Inch and the value of CM
            
            JOptionPane.showMessageDialog( null, Sum +"\nInches");  //outputs the Sum(multiplied value of CM from the inputdialog and the double Value of Inch   
            }
        catch (Exception err) {}
    }//GEN-LAST:event_jButton1ActionPerformed

    
    
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
                                                                    //METER TO FEET
        try {                                                       //try and catch statement to stop programm from sending error messages
            String Meter, Sum0;                                     //defining the variables
            Double Feet = 3.28  ;                                   //double value of feet       
            Meter = JOptionPane.showInputDialog("Meter to Feet");   //displays an input dialog for the user to input the Meter value
            Sum0 = String.valueOf(Feet * Double.valueOf(Meter));    //multiply Inch and the value of Meter

            JOptionPane.showMessageDialog( null, Sum0 +"\nFeet");   //outputs the Sum1(multipied value of Meter from the inputdialog and the double value of Feet)  
            }
        catch (Exception err) {}                                    //try and catch statement to stop programm from sending error messages
    }//GEN-LAST:event_jButton2ActionPerformed

    
    
