    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        try {                                                           //try and catch statement to stop programm from sending error messages
            String Miles, Sum3;                                         //defining the variables
            Double Km = 1.61;                                           //double value of kilometers
            Miles = JOptionPane.showInputDialog("Miles to Kilometer");  //displays an input dialog for the user to input miles value
            Sum3 = String.valueOf(Km * Double.valueOf(Miles));          //multiply Kilometers and the value of miles
                
            JOptionPane.showMessageDialog(null, Sum3+"\nKilometers");   //output the multiplied value of miles from the inputdialog and the double value of kilometers
            }
        catch (Exception err){}                                         //try and catch statement to stop programm from sending error messages
    }//GEN-LAST:event_jButton6ActionPerformed

    
    
    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        try {                                                           //try and catch statement to stop programm from sending error messages
            String Cel, Sum1;                                           //defining the variables for celcius and the sum
            Double Far = 33.8;                                          //double value of fahrenheit
            Cel = JOptionPane.showInputDialog("Celcius to Fahrenheit"); //displays an input dialog for the user to input celius value
            Sum1 = String.valueOf(Far * Double.valueOf(Cel));           //multiply celcius and fahrenheit

            JOptionPane.showMessageDialog( null, Sum1 +"\nFahrenheit"); //outputs the multipled value of celcius and fahrenheit
            }
        catch (Exception err){}                                         //try and catch statement to stop programm from sending error messages
    }//GEN-LAST:event_jButton5ActionPerformed
     
    
    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        try {                                                       //try and catch statement to stop programm from sending error messages
            String Cm1, Sum2;                                       //defining the variables
            Double Feeet = 0.033;                                   //double value of feet
            Cm1 = JOptionPane.showInputDialog("Cm to Feet");        //outputs an input box for the user to input the value of centimeter
            Sum2 = String.valueOf(Feeet * Double.valueOf(Cm1));     //multiply the double value of feet with centimeter

            JOptionPane.showMessageDialog( null, Sum2 + "\nFeet");  //outputs the multiplied value of cm and feet in a messagebox
            }
        catch (Exception err){}                                     //try and catch statement to stop programm from sending error messages
    }//GEN-LAST:event_jButton4ActionPerformed

    
    
    
    
    
    
    
    
    
    
    
    
    
    
